{"query":"mutation createUser($input: CreateUserInput!) {
	createUser(input: $input) {
		id
		email
		givenName
		familyName
		logins
	}
}","variables":{"input":{"payload":{"email":"{{ user.email  }}","password":"{{ user.password  }}","givenName":"Developer","familyName":"User"}}},"operationName":"createUser"}

{"query":"mutation loginUser($input: LoginInput!) {
	loginUser(input: $input) {
		session {
			token
			exp
		}
	}
}","variables":{"input":{"email":"{{ user.email  }}","password":"{{ user.password  }}"}},"operationName":"loginUser"}

{"query":"{
	currentUser {
		id
		givenName
		familyName
		email
		photoURL
	}
}","variables":{}}

{"query":"query checkSession($input: SessionTokenInput!) {
	checkSession(input: $input) {
		user {
			id
			email
			givenName
			familyName
			logins
		}
		session {
			id
			uid
			cre
			exp
			token
		}
	}
}","variables":{"input":{"token":"{% response 'body', 'req_2d4af28ab2c9440fb66122542c155dfd', '$.data.loginUser.session.token' %}"}},"operationName":"checkSession"}

{"query":"query allUsers($pagination: PaginationInput, $sort: UserSortInput) {
  allUsers(pagination: $pagination, sort: $sort) {
    totalCount
    edges {
      node {
        id
				email
        givenName
				familyName
      }
      cursor
    }
    pageInfo {
      hasNextPage
      endCursor
    }
  }
}
","variables":{"pagination":{"first":5}},"operationName":"allUsers"}

{"query":"query User($input: ModelIdInput!) {
	user(input: $input) {
		id
		email
		givenName
		familyName
		logins
	}
}","variables":{"input":{"id":"{% response 'body', 'req_c1a6f0573ede40c08de8be104d71adaf', '$.data.allUsers.edges.0.node.id' %}"}},"operationName":"User"}

